import React from 'react';

const Product = ({ product }) => {

  const { name, price } = product;

  return (
    <div className="Product">
      <h2>{ name }</h2>
      <p>${ price }</p>
      <button>Add to Cart</button>
    </div>
  );
}

export default Product;
